mapper.select:
select id, passwd, name, phone, type 
from Member 
where id = #{ id } and passwd = #{ passwd }

select name, content, type, address, parking, hostNo, hostId, holiday, openStart, openEnd, ok
from Host
where hostId = #{ hostId }

select name, content, type, address, parking, hostNo, hostId, holiday, openStart, openEnd, ok
from Host
where ok = #{ ok }

mapper.update:
update Member
set passwd = #{ passwd }, phone = #{ phone }
where id = #{ id }

update host
set ok = #{ ok }
where hostNo = #{ hostNo }

mapper.select:
select count(*)
from Member
where id = #{ id }
